import styleCss from "./addbook.module.css";
import React, { useState } from "react";
import { Popover, Overlay } from "react-bootstrap"
import { useRef } from "react";
import { useNavigate } from 'react-router-dom';

function Tool() {
    const [show, setShow] = useState(false);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);

    const handleClick = (event) => {
        setShow(!show);
        setTarget(event.target);
    };

    const addBook = useNavigate()
    const Click = () => {
        addBook("/transaction")
    }

    const out = useNavigate()
    const push = () => {
        out("/")
    }

    return (
        <div ref={ref}>
            <img onClick={handleClick} src="assets/ppadmin.png" alt="logo" />

            <Overlay
                show={show}
                target={target}
                placement="bottom"
                container={ref}
                containerPadding={20}
            >
                <Popover id="popover-contained">
                    <Popover.Body>
                        <div onClick={Click} className={styleCss.add}>
                            <img className={styleCss.book} src="assets/dropbook.png" alt="logo" />
                            <p className={styleCss.addBook}>Add Transaction</p>
                        </div>

                        <hr className={styleCss.garis} />

                        <div onClick={push} className={styleCss.out}>
                            <img className={styleCss.outDrop} src="assets/logoutdrop.png" alt="logo" />
                            <p className={styleCss.log}>Logout</p>
                        </div>

                    </Popover.Body>
                </Popover>
            </Overlay>
        </div>
    );
}

function AddBookPage() {
    return (
        <div className={styleCss.latar}>
            <div className={styleCss.navbar}>

                <div className={styleCss.kiri}>
                    <img className={styleCss.wow} src="assets/icon2.png" alt="Logo" />
                </div>

                <div className={styleCss.kanan}>
                    <Tool />
                </div>

            </div>

            <div className={styleCss.formGroup}>
                <p className={styleCss.head}>Add Book</p>

                <form className={styleCss.addForm}>
                    <input type="text" id="Title" placeholder="Title" className={styleCss.input} />
                    <input type="text" id="Publication Date" placeholder="Publication Date" className={styleCss.input} />
                    <input type="text" id="Page" placeholder="Page" className={styleCss.input} />
                    <input type="text" id="Author" placeholder="Author" className={styleCss.input} />
                    <input type="text" id="Isbn" placeholder="ISBN" className={styleCss.input} />
                    <textarea type="text" id="About" placeholder="About This Book" className={styleCss.about} />
                    <label className={styleCss.fileGroup}>Attache Book File</label>
                    <input type="file" id="acnu"  className={styleCss.input} />
                    <button className={styleCss.tambah} type="file" id="acnu" >Add Book</button>
                </form>

                <div className={styleCss.buttonAdd}>
                    <label className={styleCss.addGroup}>
                        <p className={styleCss.tagAdd}>Add Book</p>
                        <img className={styleCss.add1} src="assets/add.png" alt="logo" />
                    </label>
                    
                </div>
            </div>


        </div>
    )
}

export default AddBookPage;
