import Popup from './components/popup';
import { useState } from 'react';
import signinCss from ".../signin.module.css";

function App() {
  const [buttonPopup, setButtonPopup] = useState(false);

  return (
    <div className="App">
      <main>
        <h1>React Popup</h1>
        <br /><br />
        <button onClick={ () => setButtonPopup(true)}>Open Popup</button>
      </main>

      <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
        <div>
          <form className={signinCss.form}>
            <div className={signinCss.formGroup}>
              <h1 className={signinCss.header}>Sign In</h1>
              <input
                className={signinCss.inputGroup}
                id="email"
                placeholder="Email"
                type="email"
              />

              <input
                className={signinCss.inputGroup1}
                id="password"
                placeholder="Password"
                type="password"
              />

              <button className={signinCss.button} >Sign In</button>
              <p className={signinCss.text}>Don't have an account? Klik Here</p>
            </div>
          </form>
        </div>
      </Popup>
    </div>
  );
}


export default App;
