import Popup from './components/popup';

function App() {
  const { buttonPopup, setButtonPopup } = useState(false);

  return (
    <div className='App'>
      <main>
        <h1>React Popup</h1>
        <br /><br />
        <button onClick={() => setButtonPopup(true)}>Open Popup</button>
      </main>

      <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
        <h3>My P</h3>
      </Popup>
    </div>
  );
}


export default App;
